const initialState = {
    moviesFavorites: [],
    moviesLoaded: [],
    movieDetail: {}
};

function rootReducer(state = initialState, action) {
    switch(action.type) {
        case ADD_MOVIE_FAVORITE:
            return {
                ...state,
                moviesFavorites: state.moviesFavorites.concat(payload)
            }
        
        case GET_MOVIES:
            return {
                ...state,
                moviesLoaded: payload.Search
            }

        default:
            return {
                ...state
            }
    }
}

export default rootReducer;
